"use strict";

document.addEventListener('DOMContentLoaded', function () {
  // read also slider
  var readAlsoSlider = document.querySelector('.read-also__slider .swiper');
  if (readAlsoSlider) {
    var myReadAlsoSlider = new Swiper(readAlsoSlider, {
      slidesPerView: 3,
      breakpoints: {
        0: {
          slidesPerView: 'auto',
          spaceBetween: 5,
          centeredSlides: true
        },
        744: {
          slidesPerView: 'auto',
          spaceBetween: 45,
          centeredSlides: true
        },
        1024: {
          slidesPerView: 3,
          spaceBetween: 0,
          centeredSlides: false
        }
      }
    });
  }

  // image slider slider
  var imageSlider = document.querySelectorAll('.image-slider__slider .swiper');
  if (imageSlider) {
    imageSlider.forEach(function (slider) {
      var myImageSlider = new Swiper(slider, {
        slidesPerView: 1,
        loop: true,
        speed: 800,
        parallax: true,
        watchSlidesProgress: true,
        navigation: {
          prevEl: slider.closest('.image-slider').querySelector('.swiper-button-prev'),
          nextEl: slider.closest('.image-slider').querySelector('.swiper-button-next')
        },
        pagination: {
          el: slider.closest('.image-slider').querySelector('.swiper-pagination'),
          clickable: true
        }
      });
    });
  }

  // team slider
  var teamSlider = document.querySelector('.team__slider .swiper');
  if (teamSlider) {
    var myTeamSlider = new Swiper(teamSlider, {
      slidesPerView: 1,
      loop: true,
      speed: 800,
      parallax: true,
      watchSlidesProgress: true,
      navigation: {
        prevEl: teamSlider.closest('.team').querySelector('.swiper-button-prev'),
        nextEl: teamSlider.closest('.team').querySelector('.swiper-button-next')
      },
      pagination: {
        el: teamSlider.closest('.team').querySelector('.swiper-pagination'),
        clickable: true
      }
    });
  }
});